export function PlasmicRootProvider(props: PlasmicRootProviderProps) {
  const {
    platform,
    children,
    userAuthToken,
    isUserLoading,
    authRedirectUri,
    user,
    disableLoadingBoundary,
    suspenseFallback,
  } = props;
  const context = React.useMemo(
    () => ({
      platform,
