  const overrides: Overrides = {
    [config.root]: {
      as: link ? "a" : "button",
      props: {
        // Put this at the top, as user may also have set `type` as
        // inherited from "button", so let `rest` override it
        ...omit(
          rest as any,
          ...plasmicClass.internalArgProps,
          ...plasmicClass.internalVariantProps
