        type: buttonType,
        ref: ref,
        disabled: isDisabled,
        target:
          target === true ? "_blank" : target === false ? undefined : target,
        ...(!!link && { href: link }),
      },
    },
  };

  return {
    plasmicProps: {
      variants: variants as PlasmicClassVariants<C>,
      args: args as PlasmicClassArgs<C>,
      overrides: overrides as PlasmicClassOverrides<C>,
    },
  };
}
