  const overrides: Overrides = {
    [config.root]: {
      wrapChildren: (children: React.ReactNode) => (
        <TriggeredOverlayContext.Provider value={triggerContext}>
          {children}
        </TriggeredOverlayContext.Provider>
      ),
      props: {
        ref: rootRef,
