        {
          ref: onRef,
          style: noOutline(),
        }
      ),
    },
    [config.labelContainer]: {
      props: labelProps,
    },
  };

  return {
    plasmicProps: {
      variants: variants as PlasmicClassVariants<C>,
      args: args as PlasmicClassArgs<C>,
      overrides: overrides as PlasmicClassOverrides<C>,
    },
  };
}
import { Node } from "@react-types/shared";
import * as React from "react";
import { useListBoxSection } from "@react-aria/listbox";
import { useSeparator } from "@react-aria/separator";
import { pick } from "../../common";
import { Overrides } from "../../render/elements";
import { renderCollectionNode, SectionLikeProps } from "../collection-utils";
import {
  AnyPlasmicClass,
  mergeVariantToggles,
  PlasmicClassArgs,
  PlasmicClassOverrides,
  PlasmicClassVariants,
  PLUME_STRICT_MODE,
  VariantDef,
} from "../plume-utils";
import {
  getDefaultPlasmicProps,
  getStyleProps,
  StyleProps,
} from "../props-utils";
import { SelectContext } from "./context";

export interface BaseSelectOptionGroupProps
  extends SectionLikeProps,
    StyleProps {}

interface SelectOptionGroupConfig<C extends AnyPlasmicClass> {
  noTitleVariant: VariantDef<PlasmicClassVariants<C>>;
  isFirstVariant: VariantDef<PlasmicClassVariants<C>>;

  optionsSlot: keyof PlasmicClassArgs<C>;
  titleSlot: keyof PlasmicClassArgs<C>;

  root: keyof PlasmicClassOverrides<C>;
  separator: keyof PlasmicClassOverrides<C>;
  titleContainer: keyof PlasmicClassOverrides<C>;
  optionsContainer: keyof PlasmicClassOverrides<C>;
}

export function useSelectOptionGroup<
  P extends BaseSelectOptionGroupProps,
  C extends AnyPlasmicClass
>(plasmicClass: C, props: P, config: SelectOptionGroupConfig<C>) {
  const state = React.useContext(SelectContext);

  // `node` should exist if the OptionGroup was instantiated properly
  // within a Select
  const node = (props as any)._node as
    | Node<React.ReactElement<BaseSelectOptionGroupProps>>
    | undefined;

  if (!state || !node) {
    if (PLUME_STRICT_MODE) {
      throw new Error(
        "You can only use a Select.OptionGroup within a Select component."
      );
    }
    return getDefaultPlasmicProps(plasmicClass, props);
  }

  const { headingProps, groupProps } = useListBoxSection({
    heading: props.title,
    "aria-label": props["aria-label"],
  });

  const { separatorProps } = useSeparator({
    elementType: "li",
  });

  const variants = {
    ...pick(props, ...plasmicClass.internalVariantProps),
    ...mergeVariantToggles(
      { def: config.noTitleVariant, active: !props.title },
      {
        def: config.isFirstVariant,
        active: state.collection.getFirstKey() === node.key,
      }
    ),
  };

  const args = {
    ...pick(props, ...plasmicClass.internalArgProps),
    [config.titleSlot]: props.title,
    [config.optionsSlot]: Array.from(node.childNodes).map((childNode) =>
      renderCollectionNode(childNode)
    ),
  };

  const overrides: Overrides = {
    [config.root]: {
      props: getStyleProps(props),
    },
    [config.separator]: {
      props: {
        ...separatorProps,
      },
    },
    [config.titleContainer]: {
      props: {
        role: "presentation",
        ...headingProps,
      },
      ...(!props.title && {
        render: () => null,
      }),
    },
    [config.optionsContainer]: {
      props: {
        ...groupProps,
      },
    },
  };

  return {
    plasmicProps: {
      variants: variants as PlasmicClassVariants<C>,
      args: args as PlasmicClassArgs<C>,
      overrides: overrides as PlasmicClassOverrides<C>,
    },
  };
}
import { useSwitch as useAriaSwitch } from "@react-aria/switch";
import { VisuallyHidden } from "@react-aria/visually-hidden";
import { useToggleState } from "@react-stately/toggle";
import { AriaSwitchProps } from "@react-types/switch";
import * as React from "react";
import { pick } from "../../common";
import { mergeProps } from "../../react-utils";
import { Overrides } from "../../render/elements";
import { useEnsureSSRProvider } from "../../render/ssr";
import {
  AnyPlasmicClass,
  mergeVariantToggles,
  PlasmicClassArgs,
  PlasmicClassOverrides,
  PlasmicClassVariants,
  VariantDef,
} from "../plume-utils";
import { getStyleProps, StyleProps } from "../props-utils";

export type SwitchRef = React.Ref<SwitchRefValue>;
export interface SwitchRefValue extends SwitchState {
  getRoot: () => HTMLElement | null;
  focus: () => void;
  blur: () => void;
}

interface SwitchState {
  setChecked: (checked: boolean) => void;
}

export interface SwitchProps
  extends Omit<AriaSwitchProps, "isSelected" | "defaultSelected">,
    StyleProps {
  /**
   * Whether the Switch is checked or not; controlled
   */
  isChecked?: boolean;

  /**
   * Whether the Switch is checked by default; uncontrolled
   */
  defaultChecked?: boolean;
}

function asAriaSwitchProps(props: SwitchProps) {
  const ariaProps = {
    ...props,
    isSelected: props.isChecked,
    defaultSelected: props.defaultChecked,
  };
  delete ariaProps["isChecked"];
  delete ariaProps["defaultChecked"];
  return ariaProps;
}

interface SwitchConfig<C extends AnyPlasmicClass> {
  isCheckedVariant: VariantDef<PlasmicClassVariants<C>>;
  isDisabledVariant?: VariantDef<PlasmicClassVariants<C>>;
  noLabelVariant?: VariantDef<PlasmicClassVariants<C>>;
  labelSlot?: keyof PlasmicClassArgs<C>;
  root: keyof PlasmicClassOverrides<C>;
}

export function useSwitch<P extends SwitchProps, C extends AnyPlasmicClass>(
  plasmicClass: C,
  props: P,
  config: SwitchConfig<C>,
  ref: SwitchRef = null
) {
  const { children, isDisabled } = props;
  useEnsureSSRProvider();
  const inputRef = React.useRef<HTMLInputElement>(null);
  const rootRef = React.useRef<HTMLElement>(null);
  const ariaProps = asAriaSwitchProps(props);
  const state = useToggleState(ariaProps);
  const { inputProps } = useAriaSwitch(ariaProps, state, inputRef);
  const variants = {
    ...pick(props, ...plasmicClass.internalVariantProps),
    ...mergeVariantToggles(
      {
        def: config.isDisabledVariant,
        active: isDisabled,
      },
      {
        def: config.isCheckedVariant,
        active: state.isSelected,
      },
      {
        def: config.noLabelVariant,
        active: !children,
      }
    ),
  };
  const overrides: Overrides = {
    [config.root]: {
      as: "label",
      props: mergeProps(getStyleProps(props), {
        ref: rootRef,
      }),
      wrapChildren: (children: React.ReactNode) => (
        <>
          <VisuallyHidden>
            <input {...inputProps} ref={inputRef} />
          </VisuallyHidden>
          {children}
        </>
      ),
    },
  };
  const args = {
    ...pick(props, ...plasmicClass.internalArgProps),
    ...(config.labelSlot ? { [config.labelSlot]: children } : {}),
  };

  const plumeState: SwitchState = React.useMemo(
    () => ({
      setChecked: (checked: boolean) => state.setSelected(checked),
    }),
    [state]
  );

  React.useImperativeHandle(
    ref,
    () => ({
      getRoot: () => rootRef.current,
      focus: () => inputRef.current?.focus(),
      blur: () => inputRef.current?.blur(),
      setChecked: (checked) => plumeState.setChecked(checked),
    }),
    [rootRef, inputRef, plumeState]
  );

  return {
    plasmicProps: {
      variants: variants as PlasmicClassVariants<C>,
      overrides: overrides as PlasmicClassOverrides<C>,
      args: args as PlasmicClassArgs<C>,
    },
    state: plumeState,
  };
}
import * as React from "react";
import { omit, pick } from "../../common";
import { Overrides } from "../../render/elements";
import {
  AnyPlasmicClass,
  mergeVariantToggles,
  PlasmicClassArgs,
  PlasmicClassOverrides,
  PlasmicClassVariants,
  VariantDef,
} from "../plume-utils";

export interface BaseTextInputProps
  extends Omit<React.ComponentProps<"input">, "type" | "disabled">,
    PlumeTextInputProps {}

export interface PlumeTextInputProps {
  showStartIcon?: boolean;
  showEndIcon?: boolean;
  startIcon?: React.ReactNode;
  endIcon?: React.ReactNode;
  isDisabled?: boolean;
  type?: "text" | "password" | "email" | "url" | string;
  inputClassName?: string;
  inputStyle?: React.CSSProperties;
  className?: string;
  style?: React.CSSProperties;
}

export interface TextInputRefValue {
  focus: () => void;
  blur: () => void;
  getRoot: () => HTMLElement | null;
  getInput: () => HTMLInputElement | null;
}

export type TextInputRef = React.Ref<TextInputRefValue>;

interface TextInputConfig<C extends AnyPlasmicClass> {
  showStartIconVariant: VariantDef<PlasmicClassVariants<C>>;
  showEndIconVariant?: VariantDef<PlasmicClassVariants<C>>;
  isDisabledVariant?: VariantDef<PlasmicClassVariants<C>>;
  startIconSlot?: keyof PlasmicClassArgs<C>;
  endIconSlot?: keyof PlasmicClassArgs<C>;
  root: keyof PlasmicClassOverrides<C>;
  input: keyof PlasmicClassOverrides<C>;
}

export function useTextInput<
  P extends PlumeTextInputProps,
  C extends AnyPlasmicClass
>(
  plasmicClass: C,
  props: P,
  config: TextInputConfig<C>,
  ref: TextInputRef = null
) {
  const {
    isDisabled,
    startIcon,
    endIcon,
    showStartIcon,
    showEndIcon,
    className,
    style,
    inputClassName,
    inputStyle,
    ...rest
  } = props;
  const rootRef = React.useRef<HTMLElement>(null);
  const inputRef = React.useRef<HTMLInputElement>(null);

  React.useImperativeHandle(
    ref,
    () => ({
      focus() {
        inputRef.current?.focus();
      },
      blur() {
        inputRef.current?.blur();
      },
      getRoot() {
        return rootRef.current;
      },
      getInput() {
        return inputRef.current;
      },
      getBoundingClientRect() {
        return rootRef.current?.getBoundingClientRect();
      },
    }),
    [rootRef, inputRef]
  );

  const variants = {
    ...pick(props, ...plasmicClass.internalVariantProps),
    ...mergeVariantToggles(
      { def: config.showStartIconVariant, active: showStartIcon },
      { def: config.showEndIconVariant, active: showEndIcon },
      { def: config.isDisabledVariant, active: isDisabled }
    ),
  };

  const args = {
    ...pick(props, ...plasmicClass.internalArgProps),
    ...(config.startIconSlot && { [config.startIconSlot]: startIcon }),
    ...(config.endIconSlot && { [config.endIconSlot]: endIcon }),
  };

  const overrides: Overrides = {
    [config.root]: {
      props: {
        ref: rootRef,
        className,
        style,
      },
    },
    [config.input]: {
      props: {
        // We throw all of extra "rest" onto the `input` element, except props
        // that were meant for the Plasmic component -- the args and variants.
        // We make two exceptions though:
        // 1. onChange - Plume text-input is kind of screwy, and the "value" state
        //    of the input element is not exposed to the outer component via the
        //    normal way; instead, we register a separate "value" state that's
        //    in additional to the input.value state. So that means there are
        //    two `onChange` that we need to pipe through -- the one that updates
        //    `$state.input.value`, the input value state internal to the component,
        //    and the one that updates `$state.value`, the separate value state
        //    exposed to the outside. The generated <PlasmicTextInput/> will pass
        //    in the onChange for updating the internal `$state.input.value`, and
        //    props.onChange here will pass in the onChange for updating the
        //    externally exposed `$state.value`. Wow! Very sad.  If
        //    `$state.input.value` were just externally exposed, then we wouldn't
        //    need to do so; but we can't retroactively update people's TextInput
        //    components that have already been forked :-/
        // 2. `required`, because that prop existed prior to Plume pkg <= 19.1.1,
        //    but it was not linked to the input's `required` attribute as it should.
        //    So this again works around older versions of TextInput out there.
        ...omit(
          rest as any,
          ...plasmicClass.internalArgProps.filter(
            (prop) => prop !== "required" && prop !== "onChange"
          ),
          ...plasmicClass.internalVariantProps
        ),
        disabled: isDisabled,
        ref: inputRef,
        className: inputClassName,
        style: inputStyle,
      },
    },
  };

  return {
    plasmicProps: {
      variants: variants as PlasmicClassVariants<C>,
      args: args as PlasmicClassArgs<C>,
      overrides: overrides as PlasmicClassOverrides<C>,
    },
  };
}
import { usePlasmicCanvasContext } from "@plasmicapp/host";
import { FocusScope } from "@react-aria/focus";
import {
  DismissButton,
  useOverlay,
  useOverlayPosition,
} from "@react-aria/overlays";
import { DOMProps } from "@react-types/shared";
import * as React from "react";
import * as ReactDOM from "react-dom";
import { pick } from "../../common";
import {
  mergeProps,
  mergeRefs,
  useIsomorphicLayoutEffect,
} from "../../react-utils";
import { Overrides } from "../../render/elements";
import {
  AnyPlasmicClass,
  mergeVariantToggles,
  PlasmicClassArgs,
  PlasmicClassOverrides,
  PlasmicClassVariants,
  PLUME_STRICT_MODE,
  VariantDef,
} from "../plume-utils";
import {
  getDefaultPlasmicProps,
  getStyleProps,
  StyleProps,
} from "../props-utils";
import { TriggeredOverlayContext } from "./context";

export interface BaseTriggeredOverlayProps extends StyleProps, DOMProps {
  children?: React.ReactNode;
}

export interface TriggeredOverlayConfig<C extends AnyPlasmicClass> {
  isPlacedTopVariant?: VariantDef<PlasmicClassVariants<C>>;
  isPlacedBottomVariant?: VariantDef<PlasmicClassVariants<C>>;
  isPlacedLeftVariant?: VariantDef<PlasmicClassVariants<C>>;
  isPlacedRightVariant?: VariantDef<PlasmicClassVariants<C>>;

  contentSlot: keyof PlasmicClassArgs<C>;
  root: keyof PlasmicClassOverrides<C>;
}

export type TriggeredOverlayRef = React.Ref<HTMLElement>;

export function useTriggeredOverlay<
  P extends BaseTriggeredOverlayProps,
  C extends AnyPlasmicClass
>(
  plasmicClass: C,
  props: P,
  config: TriggeredOverlayConfig<C>,
  outerRef: TriggeredOverlayRef = null,
  isDismissable = true
) {
  const overlayRef = React.useRef<HTMLElement>(null);
  const onOverlayRef = mergeRefs(overlayRef, outerRef, (ref) => {
    // Workaround for https://github.com/adobe/react-spectrum/issues/1513
    // to avoid touch events leaking under the overlay when it is closed.
    if (ref) {
      ref.addEventListener("touchend", (e) => {
        e.preventDefault();
      });
    }
  });

  const context = React.useContext(TriggeredOverlayContext);

  if (!context) {
    // If no context, then we are not being correctly used.  Either complain, or
    // exit early.  It's okay to exit early and break the rules of React hooks
    // because we won't suddenly have the appropriate context anyway for this instance.
    if (PLUME_STRICT_MODE) {
      throw new Error(
        "You can only use a triggered overlay with a TriggeredOverlayContext"
      );
    }
    return getDefaultPlasmicProps(plasmicClass, props);
  }

  const { children } = props;
  const {
    triggerRef,
    placement,
    overlayMatchTriggerWidth,
    overlayMinTriggerWidth,
    overlayWidth,
    state,
  } = context;

  // Measure the width of the trigger to inform the width of the menu (below).
  const [isRendered, setRendered] = React.useState(false);
  const triggerWidth =
    triggerRef.current && (overlayMatchTriggerWidth || overlayMinTriggerWidth)
      ? triggerRef.current.offsetWidth
      : undefined;

  useIsomorphicLayoutEffect(() => {
    if (
      !isRendered &&
      triggerRef.current &&
      (overlayMatchTriggerWidth || overlayMinTriggerWidth)
    ) {
      setRendered(true);
    }
  }, [
    triggerRef,
    isRendered,
    overlayMatchTriggerWidth,
    overlayMinTriggerWidth,
  ]);

  const { overlayProps: overlayAriaProps } = useOverlay(
    {
      isOpen: state.isOpen,
      onClose: state.close,
      isDismissable,
      shouldCloseOnBlur: true,
    },
    overlayRef
  );

  const {
    overlayProps: overlayPositionProps,
    updatePosition,
    placement: placementAxis,
  } = useOverlayPosition({
    targetRef: triggerRef,
    overlayRef,
    placement: placement ?? "bottom left",
    shouldFlip: true,
    isOpen: state.isOpen,
    onClose: state.close,
    containerPadding: 0,
  });

  useIsomorphicLayoutEffect(() => {
    if (state.isOpen) {
      requestAnimationFrame(() => {
        updatePosition();
      });
    }
  }, [state.isOpen, updatePosition]);

  const overlayProps = mergeProps(
    {
      style: {
        left: "auto",
        right: "auto",
        top: "auto",
        bottom: "auto",
        position: "absolute",
        width:
          overlayWidth ?? (overlayMatchTriggerWidth ? triggerWidth : "auto"),
        minWidth: overlayMinTriggerWidth ? triggerWidth : "auto",
      },
    },
    overlayAriaProps,
    overlayPositionProps
  );

  const variants = {
    ...pick(props, ...plasmicClass.internalVariantProps),
    ...mergeVariantToggles(
      { def: config.isPlacedTopVariant, active: placementAxis === "top" },
      { def: config.isPlacedBottomVariant, active: placementAxis === "bottom" },
      { def: config.isPlacedLeftVariant, active: placementAxis === "left" },
      { def: config.isPlacedRightVariant, active: placementAxis === "right" }
    ),
  };

  const canvasCtx = usePlasmicCanvasContext();
  const args = {
    ...pick(props, ...plasmicClass.internalArgProps),
    [config.contentSlot]:
      canvasCtx && !canvasCtx.interactive ? (
        children
      ) : (
        <FocusScope restoreFocus>
          <DismissButton onDismiss={state.close} />
          {children}
          {/* We don't use the DismissButton at the end because it ends up taking up 1px space :-/ */}
          {/* <DismissButton onDismiss={state.close} /> */}
        </FocusScope>
      ),
  };

  const overrides: Overrides = {
    [config.root]: {
      props: mergeProps(overlayProps, getStyleProps(props), {
        ref: onOverlayRef,
      }),
      wrap: (root: React.ReactNode) => {
        if (typeof document !== "undefined") {
          return ReactDOM.createPortal(root, document.body);
        } else {
          // Possibly being invoked on the server during SSR; no need to
          // bother with a portal in that case.
          return root;
        }
      },
    },
  };

  return {
    plasmicProps: {
      variants: variants as PlasmicClassVariants<C>,
      args: args as PlasmicClassArgs<C>,
      overrides: overrides as PlasmicClassOverrides<C>,
    },
  };
}
